searchHelper:
function search(query, limit = 10) {
          if (!query || query.length < 2) return [];

          const lowerQuery = query.toLowerCase();
          const results = this.index
            .map(item => ({
              ...item,
              relevance: this.calculateRelevance(item, query)
            }))
            .filter(item => item.relevance > 0)
            .sort((a, b) => b.relevance - a.relevance)
            .slice(0, limit);

          return results;
        }

calculateRelevance:
calculateRelevance(item, query) {
    const lowerQuery = query.toLowerCase();
    const queryWords = lowerQuery.split(/\s+/).filter(word => word.length > 2);
    
    let score = 0;
    
    // Title matches (highest weight)
    if (item.title.toLowerCase().includes(lowerQuery)) {
      score += 100;
    }
    queryWords.forEach(word => {
      if (item.title.toLowerCase().includes(word)) {
        score += 50;
      }
    });
    
    // Description matches
    if (item.description && item.description.toLowerCase().includes(lowerQuery)) {
      score += 80;
    }
    queryWords.forEach(word => {
      if (item.description && item.description.toLowerCase().includes(word)) {
        score += 30;
      }
    });
    
    // Content matches
    if (item.content.toLowerCase().includes(lowerQuery)) {
      score += 40;
    }
    queryWords.forEach(word => {
      if (item.content.toLowerCase().includes(word)) {
        score += 10;
      }
    });
    
    // Tag matches
    if (item.tags) {
      item.tags.forEach(tag => {
        if (tag.toLowerCase().includes(lowerQuery)) {
          score += 60;
        }
        queryWords.forEach(word => {
          if (tag.toLowerCase().includes(word)) {
            score += 25;
          }
        });
      });
    }
    
    // Keywords matches
    if (item.keywords) {
      item.keywords.forEach(keyword => {
        if (keyword.toLowerCase().includes(lowerQuery)) {
          score += 60;
        }
        queryWords.forEach(word => {
          if (keyword.toLowerCase().includes(word)) {
            score += 25;
          }
        });
      });
    }
    
    // Boost certain content types
    if (item.type === 'doc' && lowerQuery.includes('guide')) score += 20;
    if (item.type === 'blog' && lowerQuery.includes('article')) score += 20;
    if (item.url.includes('certification') && lowerQuery.includes('certification')) score += 30;
    if (item.url.includes('training') && lowerQuery.includes('training')) score += 30;
    
    return score;
  }
